import React, { useState } from 'react';
import axios from 'axios';
import Swal from 'sweetalert2'; // SweetAlert for notifications
import { useNavigate } from 'react-router-dom';
import { useDispatch } from 'react-redux';
import { toggle_true_and_false_on_login } from '../../features/counter/counterSlice.js'; // Correct action import
import './Login.css';

const Login = () => {
    const dispatch = useDispatch();
    const navigate = useNavigate();

    const [formData, setFormData] = useState({
        email: '',
        password: '',
    });
    const [isChecked, setIsChecked] = useState(false); // Track checkbox status
    const [loading, setLoading] = useState(false); // Loading state for submission

    // Handle input changes
    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData((prevData) => ({
            ...prevData,
            [name]: value,
        }));
    };

    // Handle checkbox state
    const handleCheckboxChange = (e) => {
        setIsChecked(e.target.checked);
    };

    // Form submit handler
    const handleSubmit = async (e) => {
        e.preventDefault();

        // Validate that the checkbox is checked
        if (!isChecked) {
            Swal.fire({
                icon: 'error',
                title: 'Agreement Error',
                text: 'Please agree to the terms of use & privacy policy.',
            });
            return;
        }

        // Validate email and password fields
        if (!formData.email || !formData.password) {
            Swal.fire({
                icon: 'error',
                title: 'Empty Fields',
                text: 'Please fill in both email and password fields.',
            });
            return;
        }

        // Validate password (at least 8 characters, one letter, one number, one special char)
        const passwordRegex = /^(?=.*[a-zA-Z])(?=.*\d)(?=.*[@$!%*?&])[A-Za-z\d@$!%*?&]{8,}$/;
        if (!passwordRegex.test(formData.password)) {
            Swal.fire({
                icon: 'error',
                title: 'Password Error',
                text: 'Password must contain at least 8 characters, one letter, one number, and one special character.',
            });
            return;
        }

        // Validate Gmail email address
        const emailRegex = /^[a-zA-Z0-9._%+-]+@gmail\.com$/;
        if (!emailRegex.test(formData.email)) {
            Swal.fire({
                icon: 'error',
                title: 'Email Error',
                text: 'Please enter a valid Gmail address.',
            });
            return;
        }

        // Show loading spinner while submitting
        setLoading(true);

        // Submit form data to API
        try {
            const response = await axios.post('http://localhost:3000/api/auth/login', formData);
            if (response.data.token) {
                // Successful login, store token in localStorage
                localStorage.setItem('authToken', response.data.token);

                // Dispatch login status to Redux
                dispatch(toggle_true_and_false_on_login(true));

                Swal.fire({
                    icon: 'success',
                    title: 'Login Successful',
                    text: 'You have logged in successfully!',
                });

                // Redirect to home page
                navigate('/');
            } else {
                Swal.fire({
                    icon: 'error',
                    title: 'Login Failed',
                    text: 'Invalid email or password.',
                });
            }
        } catch (error) {
            Swal.fire({
                icon: 'error',
                title: 'Login Error',
                text: 'An error occurred while trying to log in. Please try again later.',
            });
            console.error('Error logging in:', error);
        } finally {
            setLoading(false); // Stop loading spinner
        }
    };

    return (
        <div className="loginsignup">
            <div className="loginsignup-container">
                <h1>Login</h1>
                <form className="loginsignup-fields" onSubmit={handleSubmit}>
                    <input
                        type="email"
                        placeholder="Email Address"
                        name="email"
                        value={formData.email}
                        onChange={handleChange}
                        required
                    />
                    <input
                        type="password"
                        placeholder="Password"
                        name="password"
                        value={formData.password}
                        onChange={handleChange}
                        required
                    />
                    <div className="loginsignup-agree">
                        <input
                            type="checkbox"
                            name="agree"
                            id="agree"
                            checked={isChecked}
                            onChange={handleCheckboxChange}
                        />
                        <label htmlFor="agree">
                            By continuing, I agree to the terms of use & privacy policy.
                        </label>
                    </div>
                    <button type="submit" disabled={!isChecked || loading}>
                        {loading ? 'Loading...' : 'Continue'}
                    </button>
                </form>
            </div>
        </div>
    );
};

export default Login;
