import { createSlice } from '@reduxjs/toolkit';

export const counterSlice = createSlice({
  name: 'counter',
  initialState: {
    token: null,       // Initialize token as null (or an empty string)
    isLoggedIn: false, // Initialize isLoggedIn as false
  },
  reducers: {
    login: (state, action) => {
      state.token = action.payload.token;  // Set token when logging in
      state.isLoggedIn = true;             // Set isLoggedIn to true on login
    },
    logout: (state) => {
      state.token = null;  // Clear token on logout
      state.isLoggedIn = false; // Set isLoggedIn to false on logout
    },
  },
});

// Export the actions generated by createSlice
export const { login, logout } = counterSlice.actions;

// Export the reducer
export default counterSlice.reducer;
