import React from 'react';
import './App.css';
import { BrowserRouter, Routes, Route, Navigate } from 'react-router-dom';
import { Provider, useSelector } from 'react-redux'; // Import Provider and useSelector
import { store } from './app/store'; // Import the store
import Header from './components/Header/Header';
import Home from './components/Home/Home';
import About from './components/About/About';
import Ourservice from './components/Ourservice/Ourservice';
import Gallary from './components/Gallary/Gallary';
import Review from './components/Review/Review';
import Contact from './components/Contact/Contact';
import Order from './components/Order/Order';
import Login from './main/Login/Login';
import Signin from './main/Signin/Signin';

function App() {
  // Access the authToken from Redux store
  const authToken = useSelector((state) => state.auth.token); // Assuming auth slice for token
  const isLoggedIn = !!authToken; // Convert authToken to a boolean value

  return (
    <Provider store={store}>
      <div className="app">
        <BrowserRouter>
          {/* Header component will be visible on all pages */}
          <Header />

          <Routes>
            {/* Public Routes */}
            <Route path="/" element={<Home />} />
            <Route path="/about" element={<About />} />
            <Route path="/ourservice" element={<Ourservice />} />
            <Route path="/gallary" element={<Gallary />} />
            <Route path="/review" element={<Review />} />
            <Route path="/contact" element={<Contact />} />

            {/* Protected Route - Only accessible if user is logged in */}
            <Route
              path="/order"
              element={isLoggedIn ? <Order /> : <Navigate to="/login" />}
            />

            {/* Authentication Routes */}
            <Route path="/login" element={<Login />} />
            <Route path="/signin" element={<Signin />} />

            {/* Catch-all for undefined routes */}
            <Route path="*" element={<Navigate to="/" />} />
          </Routes>
        </BrowserRouter>
      </div>
    </Provider>
  );
}

export default App;
