// src/features/counter/counterSlice.js

import { createSlice } from '@reduxjs/toolkit';

export const counterSlice = createSlice({
  name: 'counter',
  initialState: {
    value: 0,
    isLoggedIn: false // Assuming you want to track login status here
  },
  reducers: {
    increment: (state) => {
      state.value += 1;
    },
    decrement: (state) => {
      state.value -= 1;
    },
    incrementByAmount: (state, action) => {
      state.value += action.payload;
    },
    toggle_true_and_false_on_login: (state, action) => {
      state.isLoggedIn = action.payload;
    },
  },
});

export const { increment, decrement, incrementByAmount, toggle_true_and_false_on_login } = counterSlice.actions;

export default counterSlice.reducer;
