import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import './Header.css';
import logo2 from '../../asserts/log.png'; // Corrected path to 'asserts' folder
import LogoutButton from '../../main/Logout/LogoutButton';

const Header = () => {
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  useEffect(() => {
    // Check local storage on component mount
    const token = localStorage.getItem('authToken');
    setIsLoggedIn(!!token); // Update state based on token presence
  }, []);

  return (
    <header className="header">
      <div className="logo-container">
        <Link to="/">
          <img src={logo2} alt="Event Hub Logo" className="logo" />
        </Link>
      </div>
      <nav>
        <ul>
          <li><Link to="/">Home</Link></li>
          <li><Link to="/ourservice">Our Service</Link></li>
          <li><Link to="/about">About</Link></li>
          <li><Link to="/gallary">Gallery</Link></li>
          <li><Link to="/review">Review</Link></li>
          <li><Link to="/contact">Contact</Link></li>
          {isLoggedIn && <li><Link to="/order">Order</Link></li>} {/* Conditionally show Order link */}
          {!isLoggedIn && (
            <>
              <li><Link to="/login">Login</Link></li>
              <li><Link to="/signin">Signin</Link></li>
            </>
          )}
        </ul>
      </nav>
    </header>
  );
};

export default Header;















