import React from 'react';
import { BrowserRouter, Routes, Route, Navigate } from 'react-router-dom';
import Header from './component/Header/Header';
// import Footer from './component/Footer/Footer'; // Uncomment and update the path if you have a Footer component        
import Home from './component/Home/Home';
import Ourservice from './component/Ourservice/Ourservice';
import About from './component/About/About';
import Gallary from './component/Gallary/Gallary';
import Review from './component/Review/Review';
import Order from './component/Order/Order';
import Contact from './component/Contact/Contact';
import Login from './main/Login/Login';
import Signin from './main/Signin/Signin';
import { useSelector } from 'react-redux';
import './App.css';

function App() {
    const count = useSelector((state) => state.counter.value);
    const isLoggedIn = !!localStorage.getItem('authToken'); // Check if the user is logged in

    return (
        <div>
            <BrowserRouter>
                <Header />
                <Routes>
                    <Route path="/" element={<Home />} />
                    <Route path="/ourservice" element={<Ourservice />} />
                    <Route path="/about" element={<About />} />
                    <Route path="/gallary" element={<Gallary />} />
                    <Route path="/review" element={<Review />} />
                    <Route path="/contact" element={<Contact />} />
                    <Route path="/order" element={isLoggedIn ? <Order /> : <Navigate to="/login" />} />
                    <Route path="/login" element={<Login />} />
                    <Route path="/signin" element={<Signin />} />
                    {/* Add more routes as needed */}
                </Routes>
                {/* <Footer /> */}
            </BrowserRouter>
        </div>
    );
}

export default App;
