import { createAsyncThunk } from '@reduxjs/toolkit';
import {
  setLoading,
  setError,
  viewBookings,
  viewFeedbacks,
  setEvents,
  setTickets,
} from './eventOwnerSlice';

// Fetch Bookings
export const fetchBookings = createAsyncThunk('eventOwner/fetchBookings', async (eventId, { dispatch }) => {
  dispatch(setLoading(true));
  try {
    const response = await fetch(`/api/bookings/${eventId}`);
    const data = await response.json();
    dispatch(viewBookings(data));
  } catch (error) {
    dispatch(setError(error.message));
  } finally {
    dispatch(setLoading(false));
  }
});

// Fetch Feedbacks
export const fetchFeedbacks = createAsyncThunk('eventOwner/fetchFeedbacks', async (eventId, { dispatch }) => {
  dispatch(setLoading(true));
  try {
    const response = await fetch(`/api/feedbacks/${eventId}`);
    const data = await response.json();
    dispatch(viewFeedbacks(data));
  } catch (error) {
    dispatch(setError(error.message));
  } finally {
    dispatch(setLoading(false));
  }
});

// Create Event
export const createEvent = createAsyncThunk('eventOwner/createEvent', async (eventDetails, { dispatch }) => {
  dispatch(setLoading(true));
  try {
    const response = await fetch('/api/events', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(eventDetails),
    });
    const data = await response.json();
    // Optionally, you can update the events state here
    dispatch(setEvents(data));
  } catch (error) {
    dispatch(setError(error.message));
  } finally {
    dispatch(setLoading(false));
  }
});

// Update Event
export const updateEvent = createAsyncThunk('eventOwner/updateEvent', async ({ eventId, eventDetails }, { dispatch }) => {
  dispatch(setLoading(true));
  try {
    const response = await fetch(`/api/events/${eventId}`, {
      method: 'PUT',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(eventDetails),
    });
    const data = await response.json();
    // Handle updated events state if needed
    dispatch(setEvents(data));
  } catch (error) {
    dispatch(setError(error.message));
  } finally {
    dispatch(setLoading(false));
  }
});

// Delete Event
export const deleteEvent = createAsyncThunk('eventOwner/deleteEvent', async (eventId, { dispatch }) => {
  dispatch(setLoading(true));
  try {
    await fetch(`/api/events/${eventId}`, {
      method: 'DELETE',
    });
    // Optionally, update the events state here
    dispatch(fetchBookings()); // Refresh bookings or events
  } catch (error) {
    dispatch(setError(error.message));
  } finally {
    dispatch(setLoading(false));
  }
});

