import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { fetchAdminData, fetchUserDetailsAction, approveEventAction } from '../../../../features/admin/adminActions';
import { selectAdminDashboardData, selectUserDetails, selectAdminLoading, selectAdminError } from '../../../../features/admin/adminSelectors';

const AdminDashboard = () => {
    const dispatch = useDispatch();
    const dashboardData = useSelector(selectAdminDashboardData);
    const users = useSelector(selectUserDetails);
    const loading = useSelector(selectAdminLoading);
    const error = useSelector(selectAdminError);

    useEffect(() => {
        dispatch(fetchAdminData());
        dispatch(fetchUserDetailsAction());
    }, [dispatch]);

    const handleApproveEvent = (eventId) => {
        dispatch(approveEventAction(eventId));
    };

    if (loading) return <div>Loading...</div>;
    if (error) return <div>Error: {error}</div>;

    return (
        <div>
            <h1>Admin Dashboard</h1>
            <h2>Event Submissions</h2>
            {dashboardData && dashboardData.events.map(event => (
                <div key={event.id}>
                    <p>{event.name}</p>
                    <button onClick={() => handleApproveEvent(event.id)}>Approve Event</button>
                </div>
            ))}

            <h2>User Accounts</h2>
            {users.map(user => (
                <div key={user.id}>
                    <p>{user.name} - {user.email}</p>
                </div>
            ))}
        </div>
    );
};

export default AdminDashboard;
