import React from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import './LogoutButton.css'; // Optional, if you need additional styles

const LogoutButton = () => {
  const navigate = useNavigate();

  const handleLogout = async () => {
    try {
      await axios.post('/api/auth/logout', {}, { withCredentials: true }); // Ensure credentials are sent
      localStorage.removeItem('authToken'); // Remove token from local storage if needed
      navigate('/login'); // Redirect to login page or another page
    } catch (error) {
      console.error('Error logging out:', error);
    }
  };

  return (
    <button onClick={handleLogout} className="logout-btn"> // Add a class if needed
      Logout
    </button>
  );
};

export default LogoutButton;
