import { createSlice, createAsyncThunk } from '@reduxjs/toolkit';
import axios from 'axios';

// Async thunks
export const fetchAdminData = createAsyncThunk('admin/fetchAdminData', async () => {
    const response = await axios.get('/api/admin/dashboard');
    return response.data;
});

export const fetchUserDetails = createAsyncThunk('admin/fetchUserDetails', async () => {
    const response = await axios.get('/api/admin/users');
    return response.data;
});

// Initial state
const initialState = {
    user: null,
    token: null,
    dashboardData: null,
    users: [],
    loading: false,
    error: null,
};

// Slice
const adminSlice = createSlice({
    name: 'admin',
    initialState,
    reducers: {
        setAdminCredentials: (state, action) => {
            state.user = action.payload.user;
            state.token = action.payload.token;
        },
        logoutAdmin: (state) => {
            state.user = null;
            state.token = null;
        },
    },
    extraReducers: (builder) => {
        builder
            .addCase(fetchAdminData.pending, (state) => {
                state.loading = true;
                state.error = null;
            })
            .addCase(fetchAdminData.fulfilled, (state, action) => {
                state.loading = false;
                state.dashboardData = action.payload;
            })
            .addCase(fetchAdminData.rejected, (state, action) => {
                state.loading = false;
                state.error = action.error.message;
            })
            .addCase(fetchUserDetails.fulfilled, (state, action) => {
                state.users = action.payload;
            });
    },
});

// Export actions and reducer
export const { setAdminCredentials, logoutAdmin } = adminSlice.actions;
export default adminSlice.reducer;
