import { createSlice } from '@reduxjs/toolkit';

const initialState = {
  events: [],
  bookings: [],
  feedbacks: [],
  userProfile: {
    name: '',
    email: '',
    mobile: '',
  },
  loading: false,
  error: null,
};

const eventOwnerSlice = createSlice({
  name: 'eventOwner',
  initialState,
  reducers: {
    createEvent: (state, action) => {
      state.events.push(action.payload);
    },
    updateEvent: (state, action) => {
      const { id, updatedDetails } = action.payload;
      const eventIndex = state.events.findIndex(event => event.id === id);
      if (eventIndex !== -1) {
        state.events[eventIndex] = { ...state.events[eventIndex], ...updatedDetails };
      }
    },
    deleteEvent: (state, action) => {
      state.events = state.events.filter(event => event.id !== action.payload);
    },
    createTicket: (state, action) => {
      const { eventId, ticketDetails } = action.payload;
      const event = state.events.find(event => event.id === eventId);
      if (event) {
        event.tickets.push(ticketDetails);
      }
    },
    updateTicket: (state, action) => {
      const { eventId, ticketId, updatedDetails } = action.payload;
      const event = state.events.find(event => event.id === eventId);
      if (event) {
        const ticketIndex = event.tickets.findIndex(ticket => ticket.id === ticketId);
        if (ticketIndex !== -1) {
          event.tickets[ticketIndex] = { ...event.tickets[ticketIndex], ...updatedDetails };
        }
      }
    },
    deleteTicket: (state, action) => {
      const { eventId, ticketId } = action.payload;
      const event = state.events.find(event => event.id === eventId);
      if (event) {
        event.tickets = event.tickets.filter(ticket => ticket.id !== ticketId);
      }
    },
    viewBookings: (state, action) => {
      state.bookings = action.payload;
    },
    viewFeedbacks: (state, action) => {
      state.feedbacks = action.payload;
    },
    updateProfile: (state, action) => {
      state.userProfile = { ...state.userProfile, ...action.payload };
    },
    setLoading: (state, action) => {
      state.loading = action.payload;
    },
    setError: (state, action) => {
      state.error = action.payload;
    },
  },
});

// Export actions
export const {
  createEvent,
  updateEvent,
  deleteEvent,
  createTicket,
  updateTicket,
  deleteTicket,
  viewBookings,
  viewFeedbacks,
  updateProfile,
  setLoading,
  setError,
} = eventOwnerSlice.actions;

// Export the reducer
export default eventOwnerSlice.reducer;
