import React, { useState } from 'react';
import { useDispatch } from 'react-redux';
import { changeProfilePic } from '../../features/auth/authSlice'; // Assuming you have a change profile picture action

const ChangeProfilePic = () => {
  const dispatch = useDispatch();
  const [selectedFile, setSelectedFile] = useState(null);

  const handleFileChange = (e) => {
    setSelectedFile(e.target.files[0]);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append('profilePic', selectedFile);

    // Dispatch the change profile picture action
    dispatch(changeProfilePic(formData));
  };

  return (
    <div className="change-profile-pic">
      <h2>Change Profile Picture</h2>
      <form onSubmit={handleSubmit} encType="multipart/form-data">
        <input type="file" accept="image/*" onChange={handleFileChange} required />
        <button type="submit">Update Profile Picture</button>
      </form>
    </div>
  );
};

export default ChangeProfilePic;
