import React, { useState } from 'react';
import axios from 'axios';
import Swal from 'sweetalert2';
import { useNavigate } from 'react-router-dom';
import { useDispatch } from 'react-redux';
import { toggle_true_and_false_on_login } from '../../features/counter/counterSlice.js';
import './Login.css';

const Login = () => {
    const dispatch = useDispatch();
    const navigate = useNavigate();

    const [formData, setFormData] = useState({
        email: '',
        password: '',
    });
    const [isChecked, setIsChecked] = useState(false);
    const [loading, setLoading] = useState(false);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData((prevData) => ({
            ...prevData,
            [name]: value,
        }));
    };

    const handleCheckboxChange = (e) => {
        setIsChecked(e.target.checked);
    };

    const handleSubmit = async (e) => {
        e.preventDefault();

        // Input validation
        if (!isChecked) {
            Swal.fire({
                icon: 'error',
                title: 'Agreement Required',
                text: 'You must agree to the terms of use and privacy policy.',
            });
            return;
        }

        if (!formData.email || !formData.password) {
            Swal.fire({
                icon: 'error',
                title: 'Missing Fields',
                text: 'Please fill in both email and password fields.',
            });
            return;
        }

        const passwordRegex = /^(?=.*[a-zA-Z])(?=.*\d)(?=.*[@$!%*?&])[A-Za-z\d@$!%*?&]{8,}$/;
        if (!passwordRegex.test(formData.password)) {
            Swal.fire({
                icon: 'error',
                title: 'Invalid Password',
                text: 'Password must be at least 8 characters long, and include one letter, one number, and one special character.',
            });
            return;
        }

        const emailRegex = /^[a-zA-Z0-9._%+-]+@gmail\.com$/;
        if (!emailRegex.test(formData.email)) {
            Swal.fire({
                icon: 'error',
                title: 'Invalid Email',
                text: 'Please enter a valid Gmail address.',
            });
            return;
        }

        setLoading(true);

        try {
            const response = await axios.post('http://localhost:3000/api/auth/login', formData);
            if (response.data.token) {
                localStorage.setItem('authToken', response.data.token);
                dispatch(toggle_true_and_false_on_login(true));

                Swal.fire({
                    icon: 'success',
                    title: 'Login Successful',
                    text: 'You are now logged in!',
                });

                navigate('/');
            } else {
                Swal.fire({
                    icon: 'error',
                    title: 'Login Failed',
                    text: 'Incorrect email or password.',
                });
            }
        } catch (error) {
            Swal.fire({
                icon: 'error',
                title: 'Login Error',
                text: 'An error occurred during login. Please try again later.',
            });
            console.error('Error logging in:', error);
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="loginsignup">
            <div className="loginsignup-container">
                <h1>Login</h1>
                <form className="loginsignup-fields" onSubmit={handleSubmit}>
                    <label htmlFor="email">Email Address</label>
                    <input
                        id="email"
                        type="email"
                        name="email"
                        placeholder="Email Address"
                        value={formData.email}
                        onChange={handleChange}
                        required
                    />
                    <label htmlFor="password">Password</label>
                    <input
                        id="password"
                        type="password"
                        name="password"
                        placeholder="Password"
                        value={formData.password}
                        onChange={handleChange}
                        required
                    />
                    <div className="loginsignup-agree">
                        <input
                            id="agree"
                            type="checkbox"
                            name="agree"
                            checked={isChecked}
                            onChange={handleCheckboxChange}
                        />
                        <label htmlFor="agree">
                            By continuing, I agree to the <a href="/terms">terms of use</a> & <a href="/privacy">privacy policy</a>.
                        </label>
                    </div>
                    <button type="submit" disabled={!isChecked || loading}>
                        {loading ? 'Logging in...' : 'Login'}
                    </button>
                </form>
            </div>
        </div>
    );
};

export default Login;
