import React, { useState } from 'react';
import { Routes, Route, useNavigate } from 'react-router-dom';
import UserProfile from './UserProfile';
import ChangePassword from './ChangePassword';
import ChangeProfilePic from './ChangeProfilePic';
import './index.css'; // Assuming the styles are in index.css

const Profile = ({ user, onLogout }) => {
  const [isOpen, setIsOpen] = useState(false);
  const navigate = useNavigate();

  const toggleDropdown = () => {
    setIsOpen(!isOpen);
  };

  const handleNavigation = (path) => {
    setIsOpen(false); // Close the dropdown on navigation
    navigate(path);   // Navigate to the selected path
  };

  return (
    <div className="profile-dropdown">
      {/* Profile header with profile picture and name */}
      <div className="profile-header" onClick={toggleDropdown}>
        <img
          src={user.profilePic} // Use actual profile picture URL
          alt={user.name}
          className="profile-picture"
        />
        <span className="profile-name">{user.name}</span>
      </div>

      {/* Dropdown menu that appears on click */}
      {isOpen && (
        <div className="dropdown-menu">
          <div className="profile-info">
            <img
              src={user.profilePic} // Use actual profile picture URL
              alt={user.name}
              className="dropdown-profile-picture"
            />
            <div>
              <p className="profile-name">{user.name}</p>
              <p className="profile-role">{user.role || 'User Role'}</p>
            </div>
          </div>

          {/* Dropdown links - navigate to different routes */}
          <div className="dropdown-links">
            <button onClick={() => handleNavigation('/')}>Profile</button>
            <button onClick={() => handleNavigation('change-password')}>Change Password</button>
            <button onClick={() => handleNavigation('change-profile-pic')}>Change Profile Pic</button>
            <button onClick={onLogout}>Logout</button>
          </div>
        </div>
      )}

      {/* Routes for different profile-related components */}
      <Routes>
        <Route path="/" element={<UserProfile />} />
        <Route path="change-password" element={<ChangePassword />} />
        <Route path="change-profile-pic" element={<ChangeProfilePic />} />
      </Routes>
    </div>
  );
};

export default Profile;
