import React, { useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { changeProfilePic } from '../../features/auth/authSlice';
import './ChangeProfilePic.css';

const ChangeProfilePic = () => {
  const dispatch = useDispatch();
  const { changeProfilePicStatus, changeProfilePicError } = useSelector((state) => state.auth);
  
  const [selectedFile, setSelectedFile] = useState(null);

  const handleFileChange = (e) => {
    setSelectedFile(e.target.files[0]);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append('profilePic', selectedFile);

    // Dispatch the change profile picture action
    dispatch(changeProfilePic(formData));
  };

  return (
    <div className="change-profile-pic">
      <h2>Change Profile Picture</h2>
      <form onSubmit={handleSubmit} encType="multipart/form-data">
        {changeProfilePicStatus === 'loading' && <p>Uploading...</p>}
        {changeProfilePicStatus === 'failed' && <p className="error">{changeProfilePicError}</p>}
        {changeProfilePicStatus === 'succeeded' && <p className="success">Profile picture updated successfully!</p>}
        
        <input type="file" accept="image/*" onChange={handleFileChange} required />
        <button type="submit">Update Profile Picture</button>
      </form>
    </div>
  );
};

export default ChangeProfilePic;
